function App() {
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [toggle, setToggle] = React.useState(true);
  const [theme, setTheme] = React.useState('light');

  // Đảo ngược trạng thái đăng nhập
  const handleLoginToggle = () => {
    setIsLoggedIn(!isLoggedIn);
  };

  // Đảo ngược trạng thái hiển thị
  const handleDisplayToggle = () => {
    setToggle(!toggle);
  };

  // Đổi theme
  const handleThemeChange = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  // Tạo style dựa trên theme
  const themeStyle = {
    padding: '20px',
    backgroundColor: theme === 'light' ? '#ffffff' : '#333333',
    color: theme === 'light' ? '#000000' : '#ffffff',
    border: '1px solid #ccc',
    borderRadius: '4px'
  };

  // Style cho heading bảo đảm theo theme
  const headingStyle = {
    color: theme === 'light' ? '#000000' : '#ffffff'
  };

  // Style cho toggle content
  const toggleContentStyle = {
    margin: '10px 0',
    padding: '10px',
    backgroundColor: theme === 'light' ? '#f0f0f0' : '#555555',
    color: theme === 'light' ? '#000000' : '#ffffff',
    borderRadius: '4px'
  };

  return (
    <div style={themeStyle}>
      <h2 style={headingStyle}>Conditional Rendering trong React</h2>

      {/* Sử dụng toán tử bậc ba */}
      <div>
        <h3 style={headingStyle}>1. Sử dụng toán tử bậc ba (?:)</h3>
        <button onClick={handleLoginToggle}>
          {isLoggedIn ? 'Đăng xuất' : 'Đăng nhập'}
        </button>
        <p>
          {isLoggedIn 
            ? 'Chào mừng bạn đã đăng nhập!' 
            : 'Vui lòng đăng nhập để tiếp tục.'}
        </p>
      </div>

      {/* Sử dụng toán tử && */}
      <div>
        <h3 style={headingStyle}>2. Sử dụng toán tử &&</h3>
        <button onClick={handleDisplayToggle}>Toggle nội dung</button>
        {toggle && (
          <div style={toggleContentStyle}>
            Nội dung này sẽ ẩn/hiện khi bạn click vào nút toggle.
          </div>
        )}
      </div>

      {/* Sử dụng if trong render */}
      <div>
        <h3 style={headingStyle}>3. Đổi theme</h3>
        <button onClick={handleThemeChange}>
          Chuyển sang theme {theme === 'light' ? 'tối' : 'sáng'}
        </button>
      </div>
    </div>
  );
}
