function App() {
  const [input, setInput] = React.useState('');
  const [submitted, setSubmitted] = React.useState('');
  const [position, setPosition] = React.useState({ x: 0, y: 0 });

  // Xử lý input change
  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  // Xử lý form submit
  const handleSubmit = (event) => {
    event.preventDefault(); // Ngăn trình duyệt load lại trang
    setSubmitted(input);
  };

  // Xử lý mouse move
  const handleMouseMove = (event) => {
    setPosition({ 
      x: event.clientX, 
      y: event.clientY 
    });
  };

  return (
    <div>
      <h2>Xử lý sự kiện trong React</h2>

      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={input} 
          onChange={handleInputChange} 
          placeholder="Nhập gì đó..."
          style={{ padding: '8px', margin: '10px 0' }}
        />
        <button type="submit">Gửi</button>
      </form>

      {submitted && <p>Bạn đã nhập: {submitted}</p>}

      <div 
        onMouseMove={handleMouseMove}
        style={{ 
          marginTop: '20px',
          width: '200px', 
          height: '100px', 
          backgroundColor: '#f0f0f0',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        color: 'gray'
        }}
      >
        Di chuyển chuột ở đây
      </div>

      <p>Vị trí chuột: X={position.x}, Y={position.y}</p>
    </div>
  );
}
