// Định nghĩa component Button nhận các props với giá trị mặc định
function Button({ 
  text = 'Click me',            // Nội dung hiển thị trên nút
  onClick,                      // Hàm xử lý khi click
  variant = 'primary',          // Biến thể màu sắc (mặc định là primary)
  size = 'medium',              // Kích thước (mặc định là medium)
  disabled = false              // Trạng thái disabled (mặc định là false)
}) {
  // Các kiểu màu tương ứng với từng biến thể
  const colors = {
    primary: { bg: '#007bff', color: 'white' },
    secondary: { bg: '#6c757d', color: 'white' },
    success: { bg: '#28a745', color: 'white' },
    danger: { bg: '#dc3545', color: 'white' }
  };

  // Các kích thước nút
  const sizes = {
    small: { padding: '4px 8px', fontSize: '14px' },
    medium: { padding: '8px 16px', fontSize: '16px' },
    large: { padding: '12px 24px', fontSize: '18px' }
  };

  // Chọn màu sắc theo variant được truyền vào, nếu không có thì dùng màu primary
  const colorStyle = colors[variant] || colors.primary;

  // Chọn style kích thước theo size, nếu không có thì mặc định là medium
  const sizeStyle = sizes[size] || sizes.medium;

  // Gộp các style lại, thêm border, borderRadius, cursor, opacity
  const buttonStyle = {
    //...colorStyle,                        // Style màu
    ...sizeStyle,                         // Style kích thước
    //border: 'none',                       // Không viền
    borderRadius: '4px',                  // Bo góc
    cursor: disabled ? 'not-allowed' : 'pointer',  // Đổi con trỏ nếu bị disabled
    opacity: disabled ? 0.7 : 1           // Làm mờ nếu disabled
  };

  // Trả về nút <button> có style và behavior được điều khiển bằng props
  return (
    <button 
      style={buttonStyle}      // Gắn style động
      onClick={onClick}        // Gắn sự kiện click
      disabled={disabled}      // Trạng thái disabled
    >
      {text}                   {/* Hiển thị nội dung nút */}
    </button>
  );
}
